CREATE TABLE IF NOT EXISTS categories (
    id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL
);

CREATE TABLE IF NOT EXISTS products (
    id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT NOT NULL,
    price DECIMAL(19,2) NOT NULL,
    category_id UUID NOT NULL,
    images TEXT NOT NULL,           -- ARRAY of 2â€“3 image URLs
    rating_count INTEGER NOT NULL,    -- total number of ratings
    review_count INTEGER NOT NULL,    -- total number of reviews
    rating_1_count INTEGER NOT NULL,
    rating_2_count INTEGER NOT NULL,
    rating_3_count INTEGER NOT NULL,
    rating_4_count INTEGER NOT NULL,
    rating_5_count INTEGER NOT NULL,
    CONSTRAINT fk_category FOREIGN KEY (category_id) REFERENCES categories(id)
);

